mapper.select:
SELECT 
	COUNT(no)
FROM docs_holiday
WHERE emp_no = #{emp_no}
	AND holiday_status = #{holiday_status}

SELECT dh.no, e.name, dh.holiday_type, dh.holiday_start, dh.holiday_end, dh.holiday_status, DATE_FORMAT(holiday_reg_date,'%Y-%m-%d %H:%i') holiday_reg_date FROM docs_holiday dh,emp e WHERE dh.emp_no = e.no AND dh.emp_no = #{emp_no}
AND dh.holiday_status IN('i','a')
AND dh.holiday_status = #{holiday_status}
ORDER BY dh.no DESC LIMIT #{limitcount},#{contentnum}

mapper.update:
		UPDATE docs_holiday
        SET
            holiday_type = #{holiday_type},
            holiday_start = #{holiday_start},
            holiday_end = #{holiday_end},
            replacement = #{replacement}

                ,holiday_reason = #{holiday_reason}

        WHERE no = #{no}

mapper.select:
    	SELECT 
    		no
		FROM docs_trip
		WHERE emp_no = #{emp_no}
			AND docs_no = #{docs_no}
			AND location = #{location}
			AND bt_start = #{bt_start}
			AND bt_end = #{bt_end}

	SELECT 
		COUNT(no)
    FROM docs_trip
    WHERE emp_no = #{emp_no}
    	AND trip_status = #{trip_status}

mapper.update:
UPDATE docs_trip
SET
    docs_no = #{docs_no},
    location = #{location},
    bt_start = #{bt_start},
    bt_end = #{bt_end},
    team_cnt = #{team_cnt}

        ,food_money = #{food_money}


        ,room_charge = #{room_charge}


        ,tran_cost = #{tran_cost}


        ,tran_local_cost = #{tran_local_cost}


        ,etc = #{etc}


        ,reason = #{reason}

WHERE no = #{no}

	UPDATE docs_trip_proposer
   SET
   	dept_name = #{dept_name},
   	emp_rank = #{emp_rank},
   	name = #{name},
   	private_num = #{private_num},
   	account = #{account}

           ,replacement = #{replacement}

   WHERE proposer_no = #{proposer_no}

UPDATE docs_trip_etc
SET
   	g_num = #{g_num},
   	help = #{help},
   	b_num = #{b_num}
WHERE  etc_no = #{etc_no}

mapper.select:
SELECT dh.no, e.name, e.company, e.dept_name, e.emp_rank, dh.holiday_reg_date AS reg_date, dh.docs_type FROM emp e,docs_holiday dh WHERE e.no = dh.emp_no AND dh.holiday_status = #{docs_status} AND
e.m_approver = #{emp_no}
e.reference = #{emp_no}
UNION ALL SELECT dt.no, e.name, e.company, e.dept_name, e.emp_rank, dt.trip_reg_date AS reg_date, dt.docs_type FROM emp e, docs_trip dt WHERE e.no = dt.emp_no AND dt.trip_status = #{docs_status} AND
e.m_approver = #{emp_no}
e.reference = #{emp_no}
ORDER BY reg_date DESC LIMIT #{limitcount},#{contentnum}

	SELECT 
		COUNT(dh.no)
	FROM emp e,docs_holiday dh
	WHERE e.no = dh.emp_no
		AND dh.holiday_status = #{docs_status}
		AND

            e.m_approver = #{emp_no}


            e.reference = #{emp_no}

	UNION ALL 	
	SELECT
		COUNT(dt.no)
	FROM emp e,docs_trip dt
	WHERE e.no = dt.emp_no
		AND dt.trip_status = #{docs_status}
		AND

            e.m_approver = #{emp_no}


            e.reference = #{emp_no}

mapper.update:
UPDATE

		docs_holiday
SET
        holiday_status = #{status}


    	docs_trip
SET
         trip_status = #{status}

WHERE no = #{docs_no}
